import axios from 'axios';

// Type definitions for Product API
export interface Product {
  id: number;
  productnumber: string;
  typename: string | null;
  subtypename: string | null;
  brandname: string | null;
  model: string | null;
  price: number;
  oldprice: number | null;
  statusname: string | null;
  conditionname: string | null;
  import_status: string | null;
  color: string | null;
  country: string | null;
  size: string | null;
  dimensions: string | null;
  quantity: number;
  image_url: string | null;
  description: string | null;
  created_at: string;
  updated_at: string;
}

export interface ProductsResponse {
  items: Product[];
  total: number;
  page: number;
  per_page: number;
  pages: number;
}

export interface ProductFilters {
  brands?: string[];
  types?: string[];
  subtypes?: string[];
  colors?: string[];
  countries?: string[];
  status?: string[];
  condition?: string[];
  price_min?: number;
  price_max?: number;
  size_min?: string;
  size_max?: string;
  only_unsold?: boolean;
  search?: string;
}

export interface ProductCreate {
  productnumber: string;
  typename?: string;
  subtypename?: string;
  brandname?: string;
  model?: string;
  price?: number;
  oldprice?: number;
  statusname?: string;
  conditionname?: string;
  import_status?: string;
  color?: string;
  country?: string;
  size?: string;
  dimensions?: string;
  quantity?: number;
  image_url?: string;
  description?: string;
}

export interface ProductFiltersOptions {
  brands: string[];
  types: string[];
  colors: string[];
  countries: string[];
  statuses: string[];
  conditions: string[];
  price_range: {
    min: number;
    max: number;
  };
  size_range: {
    min: string;
    max: string;
  };
}

// API functions
export const fetchProducts = async (
  page: number = 1,
  perPage: number = 20,
  filters: ProductFilters = {}
): Promise<ProductsResponse> => {
  // Build query parameters
  const params = new URLSearchParams();
  params.append('page', page.toString());
  params.append('per_page', perPage.toString());

  // Add filters to query parameters
  if (filters.search) params.append('search', filters.search);
  if (filters.only_unsold) params.append('only_unsold', 'true');
  if (filters.price_min !== undefined) params.append('price_min', filters.price_min.toString());
  if (filters.price_max !== undefined) params.append('price_max', filters.price_max.toString());
  if (filters.size_min) params.append('size_min', filters.size_min);
  if (filters.size_max) params.append('size_max', filters.size_max);

  // Add array filters
  if (filters.brands && filters.brands.length > 0) {
    filters.brands.forEach(brand => params.append('brands', brand));
  }

  if (filters.types && filters.types.length > 0) {
    filters.types.forEach(type => params.append('types', type));
  }

  if (filters.colors && filters.colors.length > 0) {
    filters.colors.forEach(color => params.append('colors', color));
  }

  if (filters.countries && filters.countries.length > 0) {
    filters.countries.forEach(country => params.append('countries', country));
  }

  if (filters.status && filters.status.length > 0) {
    filters.status.forEach(status => params.append('status', status));
  }

  if (filters.condition && filters.condition.length > 0) {
    filters.condition.forEach(condition => params.append('condition', condition));
  }

  const response = await axios.get(`/api/products?${params.toString()}`);
  return response.data;
};

export const fetchProduct = async (id: number): Promise<Product> => {
  const response = await axios.get(`/api/products/${id}`);
  return response.data;
};

export const createProduct = async (product: ProductCreate): Promise<Product> => {
  const response = await axios.post('/api/products', product);
  return response.data;
};

export const updateProduct = async (id: number, product: Partial<ProductCreate>): Promise<Product> => {
  const response = await axios.put(`/api/products/${id}`, product);
  return response.data;
};

export const deleteProduct = async (id: number): Promise<{ message: string }> => {
  const response = await axios.delete(`/api/products/${id}`);
  return response.data;
};

export const fetchProductFilters = async (): Promise<ProductFiltersOptions> => {
  const response = await axios.get('/api/products/filters');
  return response.data;
};
