import React, { useState } from 'react';
import styled from 'styled-components';
import { useTheme } from '../../contexts/ThemeContext';
import { useSearch } from '../../App';

const FiltersContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
  padding-right: 10px;
  max-height: 100%;
  overflow-y: auto;
`;

const FiltersTitle = styled.h3`
  margin-top: 0;
  margin-bottom: 15px;
  font-size: 18px;
  padding-bottom: 10px;
  border-bottom: 1px solid #ddd;
`;

const CheckboxContainer = styled.div`
  display: flex;
  align-items: center;
  margin-top: 10px;
  margin-bottom: 10px;
  gap: 8px;
`;

const FilterSection = styled.div`
  margin-bottom: 15px;
`;

const FilterTitle = styled.h4`
  margin-top: 0;
  margin-bottom: 10px;
`;

const CheckboxLabel = styled.label`
  display: flex;
  align-items: center;
  margin-bottom: 5px;
  cursor: pointer;

  input {
    margin-right: 8px;
  }
`;

const ProductFilters: React.FC = () => {
  // Спрощений компонент без API викликів

  return (
    <FiltersContainer>
      <FiltersTitle>Фільтри товарів</FiltersTitle>

      <FilterSection>
        <FilterTitle>Бренди</FilterTitle>
        <CheckboxLabel>
          <input type="checkbox" /> Nike
        </CheckboxLabel>
        <CheckboxLabel>
          <input type="checkbox" /> Adidas
        </CheckboxLabel>
        <CheckboxLabel>
          <input type="checkbox" /> Puma
        </CheckboxLabel>
      </FilterSection>

      <FilterSection>
        <FilterTitle>Типи</FilterTitle>
        <CheckboxLabel>
          <input type="checkbox" /> Взуття
        </CheckboxLabel>
        <CheckboxLabel>
          <input type="checkbox" /> Одяг
        </CheckboxLabel>
        <CheckboxLabel>
          <input type="checkbox" /> Аксесуари
        </CheckboxLabel>
      </FilterSection>

      <FilterSection>
        <FilterTitle>Кольори</FilterTitle>
        <CheckboxLabel>
          <input type="checkbox" /> Чорний
        </CheckboxLabel>
        <CheckboxLabel>
          <input type="checkbox" /> Білий
        </CheckboxLabel>
        <CheckboxLabel>
          <input type="checkbox" /> Червоний
        </CheckboxLabel>
      </FilterSection>

      <CheckboxContainer>
        <input type="checkbox" id="unsold-only" />
        <label htmlFor="unsold-only">Тільки непродані товари</label>
      </CheckboxContainer>

      <CheckboxContainer>
        <input type="checkbox" id="visible-only" />
        <label htmlFor="visible-only">Тільки видимі товари</label>
      </CheckboxContainer>
    </FiltersContainer>
  );
};

export default ProductFilters;
