import React from 'react';
import styled from 'styled-components';
import { useSearch } from '../App';
import ProductsTable from '../components/products/ProductsTable';

const TabContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: calc(100% - 20px);
  padding-top: 10px;
`;

const MainContentArea = styled.div`
  width: 100%;
  height: 100%;
`;

// Інтерфейс для продуктів
interface Product {
  id: number;
  productnumber: string;
  clones?: string | null;
  typename: string | null;
  subtypename: string | null;
  brandname: string | null;
  model: string | null;
  marking?: string | null;
  gender?: string | null;
  color: string | null;
  description: string | null;
  country: string | null;
  manufacturer?: string | null;
  size: string | null;
  dimensions: string | null;
  price: number;
  oldprice: number | null;
  statusname: string | null;
  conditionname: string | null;
  additional_note?: string | null;
  import_status: string | null;
  quantity: number;
  image_url: string | null;
  is_visible: boolean;
  created_at: string;
  updated_at: string;
}

const ProductsTab: React.FC = () => {
  const { searchTerm } = useSearch();

  // Демонстраційні дані для продуктів
  const demoProducts: Product[] = [
    { 
      id: 1, 
      productnumber: "P001",
      typename: "Взуття",
      subtypename: "Кросівки",
      brandname: "Nike",
      model: "Air Max",
      gender: "Чоловічі",
      color: "Чорний",
      description: "Кросівки Nike Air Max",
      country: "В'єтнам",
      manufacturer: "Nike Inc.",
      size: "43",
      dimensions: "28.5",
      price: 3500,
      oldprice: 4000,
      statusname: "В наявності",
      conditionname: "Нове",
      import_status: "Імпортовано",
      quantity: 1,
      image_url: null,
      is_visible: true,
      created_at: "2023-05-20",
      updated_at: "2023-05-20"
    },
    { 
      id: 2, 
      productnumber: "P002",
      typename: "Одяг",
      subtypename: "Футболка",
      brandname: "Adidas",
      model: "Sportswear",
      gender: "Жіночі",
      color: "Білий",
      description: "Футболка Adidas біла",
      country: "Китай",
      manufacturer: "Adidas AG",
      size: "M",
      dimensions: "-",
      price: 1200,
      oldprice: null,
      statusname: "В наявності",
      conditionname: "Нове",
      import_status: "Імпортовано",
      quantity: 1,
      image_url: null,
      is_visible: true,
      created_at: "2023-05-21",
      updated_at: "2023-05-21"
    }
  ];

  // Фільтрація продуктів за пошуковим запитом
  const filteredProducts = searchTerm
    ? demoProducts.filter(product => 
        product.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.brandname?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.typename?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.productnumber.includes(searchTerm)
      )
    : demoProducts;

  // Обробники дій з продуктами
  const handleViewProduct = (productId: number) => {
    console.log('View product', productId);
  };

  const handleEditProduct = (productId: number) => {
    console.log('Edit product', productId);
  };

  const handleDeleteProduct = (productId: number) => {
    console.log('Delete product', productId);
  };

  const handleToggleVisibility = (productId: number) => {
    console.log('Toggle visibility', productId);
  };

  return (
    <TabContainer>
      <MainContentArea>
        <ProductsTable 
          products={filteredProducts}
          onViewProduct={handleViewProduct}
          onEditProduct={handleEditProduct}
          onDeleteProduct={handleDeleteProduct}
          onToggleVisibility={handleToggleVisibility}
        />
      </MainContentArea>
    </TabContainer>
  );
};

export default ProductsTab;
