import React, { useState, useEffect } from 'react';
import { useQuery, useQueryClient, useMutation } from '@tanstack/react-query';
import styled from 'styled-components';

import { fetchProducts, fetchProductFilters, toggleProductVisibility, ProductFilters } from '../services/productService';
import { useTheme } from '../contexts/ThemeContext';
import { useSearch, useFilter } from '../App';
import ProductsTable from '../components/products/ProductsTable';
import Pagination from '../components/common/Pagination';
import LoadingSpinner from '../components/common/LoadingSpinner';

const TabContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
`;

const Title = styled.h1`
  font-size: 24px;
  margin: 0;
`;

const SearchContainer = styled.div`
  position: relative;
  flex: 1;
  max-width: 500px;
  margin: 0 20px;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 10px 15px 10px 40px;
  border-radius: 4px;
  border: 1px solid ${props => props.theme.inputBorder};
  background-color: ${props => props.theme.inputBackground};
  color: ${props => props.theme.text};
  transition: border-color 0.2s;
  font-size: 16px;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.primary};
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 12px;
  top: 50%;
  transform: translateY(-50%);
  color: #888;
`;

const MainContentArea = styled.div`
  width: 100%;
`;

const ProductsCount = styled.div`
  margin-top: 20px;
  font-size: 14px;
  color: ${props => props.theme.text};
  opacity: 0.7;
`;

const ProductsTab: React.FC = () => {
  const { darkTheme } = useTheme();
  const { searchTerm } = useSearch();
  const { sidebarOpen } = useFilter();
  const [page, setPage] = useState(1);
  const [filters, setFilters] = useState<ProductFilters>({});

  // Reset to first page when search changes
  useEffect(() => {
    setPage(1);
  }, [searchTerm]);

  // Fetch products based on current filters and pagination
  const { data: productsData, isLoading: isLoadingProducts } = useQuery({
    queryKey: ['products', page, filters, searchTerm],
    queryFn: () => fetchProducts(page, 20, { ...filters, search: searchTerm }),
    keepPreviousData: true, // Keep old data while fetching new data
  });

  // Get QueryClient instance
  const queryClient = useQueryClient();

  // Toggle product visibility mutation
  const toggleVisibilityMutation = useMutation({
    mutationFn: toggleProductVisibility,
    onSuccess: () => {
      // Якщо успішно змінено видимість, оновлюємо дані
      queryClient.invalidateQueries({ queryKey: ['products'] });
    }
  });

  // Handler for toggling product visibility
  const handleToggleVisibility = (productId: number) => {
    toggleVisibilityMutation.mutate(productId);
  };

  // Handle page change
  const handlePageChange = (newPage: number) => {
    setPage(newPage);
  };

  return (
    <TabContainer>
      <Header>
        <Title>Products</Title>
        <SearchContainer>
          <SearchIcon>
            <i className="fas fa-search"></i>
          </SearchIcon>
          <SearchInput
            type="text"
            placeholder="Search products..."
            value={searchTerm || ''}
            onChange={(e) => {
              // Use the search context to update the search term
              if (useSearch() && useSearch().setSearchTerm) {
                useSearch().setSearchTerm(e.target.value);
              }
            }}
          />
        </SearchContainer>
      </Header>

      <MainContentArea>
        {isLoadingProducts ? (
          <LoadingSpinner />
        ) : (
          <>
            <ProductsTable 
              products={productsData?.items || []} 
              onViewProduct={(id) => console.log('View product', id)}
              onEditProduct={(id) => console.log('Edit product', id)}
              onDeleteProduct={(id) => console.log('Delete product', id)}
              onToggleVisibility={handleToggleVisibility}
            />

            {productsData && (
              <>
                <Pagination
                  currentPage={page}
                  totalPages={productsData.pages}
                  onPageChange={handlePageChange}
                />
                <ProductsCount>
                  Showing {productsData.items.length} of {productsData.total} products
                </ProductsCount>
              </>
            )}
          </>
        )}
      </MainContentArea>
    </TabContainer>
  );
};

export default ProductsTab;
