import React, { createContext, useContext, useState } from 'react';
import { BrowserRouter } from 'react-router-dom';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFilter } from '@fortawesome/free-solid-svg-icons';
import ProductsTab from './pages/ProductsTab';
import OrdersTab from './pages/OrdersTab';
import ProductFilters from './components/filters/ProductFilters';
import OrderFilters from './components/filters/OrderFilters';

// Створюємо контекст пошуку
interface SearchContextType {
  searchTerm: string;
  setSearchTerm: React.Dispatch<React.SetStateAction<string>>;
}

const SearchContext = createContext<SearchContextType>({
  searchTerm: '',
  setSearchTerm: () => {},
});

export const useSearch = () => useContext(SearchContext);

// Створюємо контекст фільтрів
interface FilterContextType {
  sidebarOpen: boolean;
  setSidebarOpen: React.Dispatch<React.SetStateAction<boolean>>;
}

const FilterContext = createContext<FilterContextType>({
  sidebarOpen: true,
  setSidebarOpen: () => {},
});

export const useFilter = () => useContext(FilterContext);

// Стильові компоненти
const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background-color: #f5f5f5;
`;

const Header = styled.header`
  display: flex;
  align-items: center;
  height: 60px;
  padding: 0 20px;
  background-color: #333;
  color: white;
`;

const TabContainer = styled.div`
  display: flex;
  gap: 10px;
  margin-left: 20px;
`;

const Tab = styled.div<{ active: boolean }>`
  padding: 8px 16px;
  cursor: pointer;
  border-radius: 4px;
  background-color: ${props => props.active ? '#4a6da7' : 'transparent'};
  color: white;
  font-weight: ${props => props.active ? 'bold' : 'normal'};
  transition: all 0.2s ease;

  &:hover {
    background-color: ${props => props.active ? '#4a6da7' : '#444'};
  }
`;

const MainContainer = styled.div`
  display: flex;
  flex: 1;
  overflow: hidden;
`;

const Sidebar = styled.div<{ isOpen: boolean }>`
  width: ${props => props.isOpen ? '300px' : '0'};
  height: 100%;
  background-color: #f9f9f9;
  overflow-y: auto;
  transition: width 0.3s ease;
  border-right: 1px solid #e0e0e0;
  padding: ${props => props.isOpen ? '20px' : '0'};
`;

const FilterButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #4a6da7;
  color: white;
  border: none;
  border-radius: 4px;
  padding: 8px 12px;
  margin-left: auto;
  cursor: pointer;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #3a5d97;
  }

  svg {
    margin-right: 5px;
  }
`;

const ContentArea = styled.main`
  flex: 1;
  padding: 20px;
  overflow-y: auto;
  background-color: white;
`;

// Основний компонент додатку
const App: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeTab, setActiveTab] = useState<'products' | 'orders'>('products');

  return (
    <BrowserRouter>
      <SearchContext.Provider value={{ searchTerm, setSearchTerm }}>
        <FilterContext.Provider value={{ sidebarOpen, setSidebarOpen }}>
          <AppContainer>
            <Header>
              <div>Inventory Manager</div>
              <TabContainer>
                <Tab 
                  active={activeTab === 'products'} 
                  onClick={() => setActiveTab('products')}
                >
                  Товари
                </Tab>
                <Tab 
                  active={activeTab === 'orders'} 
                  onClick={() => setActiveTab('orders')}
                >
                  Замовлення
                </Tab>
              </TabContainer>
              <FilterButton onClick={() => setSidebarOpen(!sidebarOpen)}>
                <FontAwesomeIcon icon={faFilter} />
                Фільтри
              </FilterButton>
            </Header>
            <MainContainer>
              <Sidebar isOpen={sidebarOpen}>
                {activeTab === 'products' ? <ProductFilters /> : <OrderFilters />}
              </Sidebar>
              <ContentArea>
                {activeTab === 'products' ? <ProductsTab /> : <OrdersTab />}
              </ContentArea>
            </MainContainer>
          </AppContainer>
        </FilterContext.Provider>
      </SearchContext.Provider>
    </BrowserRouter>
  );
};

export default App;
