import React, { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';

import { fetchOrders, OrderFilters } from '../services/orderService';
import { fetchOrderStatuses, fetchPaymentStatuses, fetchDeliveryMethods } from '../services/referenceService';
import { useTheme } from '../contexts/ThemeContext';
import OrdersTable from '../components/orders/OrdersTable';
import FilterSection from '../components/common/FilterSection';
import CollapsibleSection from '../components/common/CollapsibleSection';
import RangeSlider from '../components/common/RangeSlider';
import Pagination from '../components/common/Pagination';
import LoadingSpinner from '../components/common/LoadingSpinner';

const TabContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
`;

const Title = styled.h1`
  font-size: 24px;
  margin: 0;
`;

const SearchContainer = styled.div`
  position: relative;
  flex: 1;
  max-width: 500px;
  margin: 0 20px;
`;

const SearchInput = styled.input<{ isDarkTheme: boolean }>`
  width: 100%;
  padding: 10px 15px 10px 40px;
  border-radius: 4px;
  border: 1px solid ${props => props.isDarkTheme ? 'var(--dark-border-color)' : 'var(--light-border-color)'};
  background-color: ${props => props.isDarkTheme ? '#333' : 'white'};
  color: ${props => props.isDarkTheme ? 'var(--dark-text-color)' : 'var(--light-text-color)'};
  transition: border-color 0.2s;
  font-size: 16px;

  &:focus {
    outline: none;
    border-color: var(--light-accent-color);
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 12px;
  top: 50%;
  transform: translateY(-50%);
  color: #888;
`;

const FiltersContainer = styled.div`
  display: flex;
  gap: 20px;
`;

const FiltersColumn = styled.div`
  flex: 1;
  max-width: 300px;
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const MainContentArea = styled.div`
  flex: 3;
`;

const ColoredCheckbox = styled.div<{ color: string }>`
  display: flex;
  align-items: center;
  margin-bottom: 8px;

  .checkbox-label {
    position: relative;
    padding-left: 32px;
    cursor: pointer;
    user-select: none;

    &:before {
      content: '';
      position: absolute;
      left: 0;
      top: 0;
      width: 18px;
      height: 18px;
      border-radius: 3px;
      background-color: ${props => props.color};
      border: 1px solid rgba(0, 0, 0, 0.2);
    }
  }

  input {
    position: absolute;
    opacity: 0;
    cursor: pointer;

    &:checked + .checkbox-label:after {
      content: '✓';
      position: absolute;
      left: 4px;
      top: -2px;
      color: ${props => props.color.startsWith('#fff') || props.color.startsWith('#f') ? 'black' : 'white'};
      font-weight: bold;
    }
  }
`;

const OrdersCount = styled.div`
  margin-top: 20px;
  font-size: 14px;
  color: #666;
`;

const OrdersTab: React.FC = () => {
  const { darkTheme } = useTheme();
  const [page, setPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState<OrderFilters>({});
  const currentYear = new Date().getFullYear();

  // Fetch reference data for filters
  const { data: orderStatuses, isLoading: isLoadingOrderStatuses } = useQuery({
    queryKey: ['orderStatuses'],
    queryFn: fetchOrderStatuses,
  });

  const { data: paymentStatuses, isLoading: isLoadingPaymentStatuses } = useQuery({
    queryKey: ['paymentStatuses'],
    queryFn: fetchPaymentStatuses,
  });

  const { data: deliveryMethods, isLoading: isLoadingDeliveryMethods } = useQuery({
    queryKey: ['deliveryMethods'],
    queryFn: fetchDeliveryMethods,
  });

  // Fetch orders based on current filters and pagination
  const { data: ordersData, isLoading: isLoadingOrders } = useQuery({
    queryKey: ['orders', page, filters, searchTerm],
    queryFn: () => fetchOrders(page, 20, { ...filters, search: searchTerm }),
    keepPreviousData: true,
  });

  // Handle search input change
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
    setPage(1); // Reset to first page when search changes
  };

  // Update status filters
  const handleStatusChange = (statusIds: number[]) => {
    setFilters(prev => ({
      ...prev,
      order_status_ids: statusIds.length ? statusIds : undefined,
    }));
    setPage(1);
  };

  // Update payment status filters
  const handlePaymentStatusChange = (statusIds: number[]) => {
    setFilters(prev => ({
      ...prev,
      payment_status_ids: statusIds.length ? statusIds : undefined,
    }));
    setPage(1);
  };

  // Update delivery method filters
  const handleDeliveryMethodChange = (methodIds: number[]) => {
    setFilters(prev => ({
      ...prev,
      delivery_method_ids: methodIds.length ? methodIds : undefined,
    }));
    setPage(1);
  };

  // Update month range filter
  const handleMonthRangeChange = (min: number, max: number) => {
    setFilters(prev => ({
      ...prev,
      month_min: min,
      month_max: max,
    }));
    setPage(1);
  };

  // Update year range filter
  const handleYearRangeChange = (min: number, max: number) => {
    setFilters(prev => ({
      ...prev,
      year_min: min,
      year_max: max,
    }));
    setPage(1);
  };

  // Handle page change
  const handlePageChange = (newPage: number) => {
    setPage(newPage);
  };

  // Loading state
  if (isLoadingOrderStatuses || isLoadingPaymentStatuses || isLoadingDeliveryMethods) {
    return <LoadingSpinner />;
  }

  return (
    <TabContainer>
      <Header>
        <Title>Orders</Title>
        <SearchContainer>
          <SearchIcon>
            <FontAwesomeIcon icon={faSearch} />
          </SearchIcon>
          <SearchInput
            type="text"
            placeholder="Search orders..."
            value={searchTerm}
            onChange={handleSearchChange}
            isDarkTheme={darkTheme}
          />
        </SearchContainer>
      </Header>

      <FiltersContainer>
        <FiltersColumn>
          {orderStatuses && (
            <CollapsibleSection title="Order Status">
              <div>
                {orderStatuses.items.map(status => (
                  <ColoredCheckbox key={status.id} color={status.color_code || '#e0e0e0'}>
                    <input
                      type="checkbox"
                      id={`status-${status.id}`}
                      checked={filters.order_status_ids?.includes(status.id) || false}
                      onChange={(e) => {
                        const currentIds = filters.order_status_ids || [];
                        if (e.target.checked) {
                          handleStatusChange([...currentIds, status.id]);
                        } else {
                          handleStatusChange(currentIds.filter(id => id !== status.id));
                        }
                      }}
                    />
                    <label htmlFor={`status-${status.id}`} className="checkbox-label">
                      {status.name}
                    </label>
                  </ColoredCheckbox>
                ))}
              </div>
            </CollapsibleSection>
          )}

          {paymentStatuses && (
            <CollapsibleSection title="Payment Status">
              <div>
                {paymentStatuses.items.map(status => (
                  <ColoredCheckbox key={status.id} color={status.color_code || '#e0e0e0'}>
                    <input
                      type="checkbox"
                      id={`payment-${status.id}`}
                      checked={filters.payment_status_ids?.includes(status.id) || false}
                      onChange={(e) => {
                        const currentIds = filters.payment_status_ids || [];
                        if (e.target.checked) {
                          handlePaymentStatusChange([...currentIds, status.id]);
                        } else {
                          handlePaymentStatusChange(currentIds.filter(id => id !== status.id));
                        }
                      }}
                    />
                    <label htmlFor={`payment-${status.id}`} className="checkbox-label">
                      {status.name}
                    </label>
                  </ColoredCheckbox>
                ))}
              </div>
            </CollapsibleSection>
          )}

          {deliveryMethods && (
            <CollapsibleSection title="Delivery Method">
              <div>
                {deliveryMethods.items.map(method => (
                  <ColoredCheckbox key={method.id} color={method.color_code || '#e0e0e0'}>
                    <input
                      type="checkbox"
                      id={`delivery-${method.id}`}
                      checked={filters.delivery_method_ids?.includes(method.id) || false}
                      onChange={(e) => {
                        const currentIds = filters.delivery_method_ids || [];
                        if (e.target.checked) {
                          handleDeliveryMethodChange([...currentIds, method.id]);
                        } else {
                          handleDeliveryMethodChange(currentIds.filter(id => id !== method.id));
                        }
                      }}
                    />
                    <label htmlFor={`delivery-${method.id}`} className="checkbox-label">
                      {method.name}
                    </label>
                  </ColoredCheckbox>
                ))}
              </div>
            </CollapsibleSection>
          )}

          <CollapsibleSection title="Month Range">
            <RangeSlider
              min={1}
              max={12}
              value={[
                filters.month_min ?? 1,
                filters.month_max ?? 12
              ]}
              onChange={handleMonthRangeChange}
              formatLabel={value => {
                const months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
                return months[value - 1];
              }}
            />
          </CollapsibleSection>

          <CollapsibleSection title="Year Range">
            <RangeSlider
              min={currentYear - 5}
              max={currentYear}
              value={[
                filters.year_min ?? currentYear - 5,
                filters.year_max ?? currentYear
              ]}
              onChange={handleYearRangeChange}
              formatLabel={value => `${value}`}
            />
          </CollapsibleSection>
        </FiltersColumn>

        <MainContentArea>
          {isLoadingOrders ? (
            <LoadingSpinner />
          ) : (
            <>
              <OrdersTable 
                orders={ordersData?.items || []} 
                orderStatuses={orderStatuses?.items || []}
                paymentStatuses={paymentStatuses?.items || []}
                deliveryMethods={deliveryMethods?.items || []}
              />

              {ordersData && (
                <>
                  <Pagination
                    currentPage={page}
                    totalPages={ordersData.pages}
                    onPageChange={handlePageChange}
                  />
                  <OrdersCount>
                    Showing {ordersData.items.length} of {ordersData.total} orders
                  </OrdersCount>
                </>
              )}
            </>
          )}
        </MainContentArea>
      </FiltersContainer>
    </TabContainer>
  );
};

export default OrdersTab;
