import React, { useState, useMemo } from 'react';
import { useQuery, useQueryClient, useMutation } from '@tanstack/react-query';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faFilter, faChevronDown, faChevronUp } from '@fortawesome/free-solid-svg-icons';

import { fetchProducts, fetchProductFilters, toggleProductVisibility, ProductFilters } from '../services/productService';
import { useTheme } from '../contexts/ThemeContext';
import ProductsTable from '../components/products/ProductsTable';
import FilterSection from '../components/common/FilterSection';
import RangeSlider from '../components/common/RangeSlider';
import CollapsibleSection from '../components/common/CollapsibleSection';
import Pagination from '../components/common/Pagination';
import LoadingSpinner from '../components/common/LoadingSpinner';

const TabContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
`;

const Title = styled.h1`
  font-size: 24px;
  margin: 0;
`;

const SearchContainer = styled.div`
  position: relative;
  flex: 1;
  max-width: 500px;
  margin: 0 20px;
`;

const SearchInput = styled.input<{ isDarkTheme: boolean }>`
  width: 100%;
  padding: 10px 15px 10px 40px;
  border-radius: 4px;
  border: 1px solid ${props => props.isDarkTheme ? 'var(--dark-border-color)' : 'var(--light-border-color)'};
  background-color: ${props => props.isDarkTheme ? '#333' : 'white'};
  color: ${props => props.isDarkTheme ? 'var(--dark-text-color)' : 'var(--light-text-color)'};
  transition: border-color 0.2s;
  font-size: 16px;

  &:focus {
    outline: none;
    border-color: var(--light-accent-color);
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 12px;
  top: 50%;
  transform: translateY(-50%);
  color: #888;
`;

const FiltersContainer = styled.div`
  display: flex;
  gap: 20px;
`;

const FiltersColumn = styled.div`
  flex: 1;
  max-width: 300px;
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const MainContentArea = styled.div`
  flex: 3;
`;

const UnSoldCheckbox = styled.div`
  display: flex;
  align-items: center;
  margin-top: 10px;
  margin-bottom: 20px;
  gap: 8px;
`;

const ProductsCount = styled.div`
  margin-top: 20px;
  font-size: 14px;
  color: #666;
`;

const ProductsTab: React.FC = () => {
  const { darkTheme } = useTheme();
  const [page, setPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState<ProductFilters>({
    // Removing the visible_only filter to show all products by default
  });

  // Fetch product filters for filter sections
  const { data: filterOptions, isLoading: isLoadingFilters } = useQuery({
    queryKey: ['productFilters'],
    queryFn: fetchProductFilters,
  });

  // Fetch products based on current filters and pagination
  const { data: productsData, isLoading: isLoadingProducts } = useQuery({
    queryKey: ['products', page, filters, searchTerm],
    queryFn: () => fetchProducts(page, 20, { ...filters, search: searchTerm }),
    keepPreviousData: true, // Keep old data while fetching new data
  });

  // Get QueryClient instance
  const queryClient = useQueryClient();

  // Toggle product visibility mutation
  const toggleVisibilityMutation = useMutation({
    mutationFn: toggleProductVisibility,
    onSuccess: () => {
      // Якщо успішно змінено видимість, оновлюємо дані
      queryClient.invalidateQueries({ queryKey: ['products'] });
    }
  });

  // Handler for toggling product visibility
  const handleToggleVisibility = (productId: number) => {
    toggleVisibilityMutation.mutate(productId);
  };

  // Handle search input change
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
    setPage(1); // Reset to first page when search changes
  };

  // Update filters with checkbox selections
  const handleFilterChange = (filterType: keyof ProductFilters, values: string[]) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: values.length ? values : undefined,
    }));
    setPage(1); // Reset to first page when filters change
  };

  // Update range filters
  const handleRangeChange = (filterType: 'price_min' | 'price_max' | 'size_min' | 'size_max', value: number | string) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: value,
    }));
    setPage(1); // Reset to first page when filters change
  };

  // Toggle only unsold products filter
  const handleUnsoldToggle = (checked: boolean) => {
    setFilters(prev => ({
      ...prev,
      only_unsold: checked
    }));
    setPage(1);
  };

  // Toggle only visible products filter
  const handleVisibleToggle = (checked: boolean) => {
    setFilters(prev => ({
      ...prev,
      visible_only: checked
    }));
    setPage(1);
  };

  // Handle page change
  const handlePageChange = (newPage: number) => {
    setPage(newPage);
  };

  // Loading and empty states
  if (isLoadingFilters) {
    return <LoadingSpinner />;
  }

  return (
    <TabContainer>
      <Header>
        <Title>Products</Title>
        <SearchContainer>
          <SearchIcon>
            <FontAwesomeIcon icon={faSearch} />
          </SearchIcon>
          <SearchInput
            type="text"
            placeholder="Search products..."
            value={searchTerm}
            onChange={handleSearchChange}
            isDarkTheme={darkTheme}
          />
        </SearchContainer>
      </Header>

      <FiltersContainer>
        {filterOptions && (
          <FiltersColumn>
            <CollapsibleSection title="Brand">
              <FilterSection
                title="Brands"
                items={filterOptions.brands.map(brand => ({ id: brand, label: brand }))}
                selectedItems={filters.brands || []}
                onChange={(values) => handleFilterChange('brands', values)}
              />
            </CollapsibleSection>

            <CollapsibleSection title="Type">
              <FilterSection
                title="Types"
                items={filterOptions.types.map(type => ({ id: type, label: type }))}
                selectedItems={filters.types || []}
                onChange={(values) => handleFilterChange('types', values)}
              />
            </CollapsibleSection>

            <CollapsibleSection title="Color">
              <FilterSection
                title="Colors"
                items={filterOptions.colors.map(color => ({ id: color, label: color }))}
                selectedItems={filters.colors || []}
                onChange={(values) => handleFilterChange('colors', values)}
              />
            </CollapsibleSection>

            <CollapsibleSection title="Country">
              <FilterSection
                title="Countries"
                items={filterOptions.countries.map(country => ({ id: country, label: country }))}
                selectedItems={filters.countries || []}
                onChange={(values) => handleFilterChange('countries', values)}
              />
            </CollapsibleSection>

            <CollapsibleSection title="Price Range">
              <RangeSlider
                min={filterOptions.price_range.min}
                max={filterOptions.price_range.max}
                defaultMin={filters.price_min ?? filterOptions.price_range.min}
                defaultMax={filters.price_max ?? filterOptions.price_range.max}
                onChange={(min, max) => {
                  handleRangeChange('price_min', min);
                  handleRangeChange('price_max', max);
                }}
                formatValue={(value: number) => `${value} ₴`}
              />
            </CollapsibleSection>

            <UnSoldCheckbox>
              <input
                type="checkbox"
                id="unsold-only"
                checked={!!filters.only_unsold}
                onChange={(e) => handleUnsoldToggle(e.target.checked)}
              />
              <label htmlFor="unsold-only">Тільки непродані товари</label>
            </UnSoldCheckbox>

            <UnSoldCheckbox>
              <input
                type="checkbox"
                id="visible-only"
                checked={!!filters.visible_only}
                onChange={(e) => handleVisibleToggle(e.target.checked)}
              />
              <label htmlFor="visible-only">Тільки видимі товари</label>
            </UnSoldCheckbox>
          </FiltersColumn>
        )}

        <MainContentArea>
          {isLoadingProducts ? (
            <LoadingSpinner />
          ) : (
            <>
              <ProductsTable 
                products={productsData?.items || []} 
                onViewProduct={(id) => console.log('View product', id)}
                onEditProduct={(id) => console.log('Edit product', id)}
                onDeleteProduct={(id) => console.log('Delete product', id)}
                onToggleVisibility={handleToggleVisibility}
              />

              {productsData && (
                <>
                  <Pagination
                    currentPage={page}
                    totalPages={productsData.pages}
                    onPageChange={handlePageChange}
                  />
                  <ProductsCount>
                    Showing {productsData.items.length} of {productsData.total} products
                  </ProductsCount>
                </>
              )}
            </>
          )}
        </MainContentArea>
      </FiltersContainer>
    </TabContainer>
  );
};

export default ProductsTab;
