import React, { useState, Suspense } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AppThemeProvider, useTheme } from './contexts/ThemeContext';
import GlobalStyle from './styles/GlobalStyle'; // For potential global styles
import './App.css';
import './index.css'; // Main Tailwind CSS import

// Lazy load pages
const ProductsPage = React.lazy(() => import('./pages/ProductsPage'));
const OrdersPage = React.lazy(() => import('./pages/OrdersPage'));
const ClientsPage = React.lazy(() => import('./pages/ClientsPage'));
const SuppliersPage = React.lazy(() => import('./pages/SuppliersPage'));
const DeliveriesPage = React.lazy(() => import('./pages/DeliveriesPage'));

const ThemeSwitcherButton: React.FC = () => {
  const { theme, toggleTheme } = useTheme();
  return (
    <button
      onClick={toggleTheme}
      aria-label={theme === 'dark' ? 'Switch to light mode' : 'Switch to dark mode'}
      className="p-2 rounded-md bg-gray-200 hover:bg-gray-300 dark:bg-gray-700 dark:hover:bg-gray-600 text-gray-800 dark:text-gray-200 transition-colors duration-150"
    >
      {theme === 'dark' ? '‚òÄÔ∏è' : 'üåô'}
    </button>
  );
};

type TabKey = 'products' | 'orders' | 'clients' | 'suppliers' | 'deliveries';

interface TabConfig {
  key: TabKey;
  label: string;
  component: React.LazyExoticComponent<React.ComponentType<any>>;
}

const TABS: TabConfig[] = [
  { key: 'products', label: '–¢–æ–≤–∞—Ä–∏', component: ProductsPage },
  { key: 'orders', label: '–ó–∞–º–æ–≤–ª–µ–Ω–Ω—è', component: OrdersPage },
  { key: 'clients', label: '–ö–ª—ñ—î–Ω—Ç–∏', component: ClientsPage },
  { key: 'suppliers', label: '–ü–æ—Å—Ç–∞—á–∞–ª—å–Ω–∏–∫–∏', component: SuppliersPage },
  { key: 'deliveries', label: '–ü–æ—Å—Ç–∞–≤–∫–∏', component: DeliveriesPage },
];

const AppContent: React.FC = () => {
  const [activeTab, setActiveTab] = useState<TabKey>(TABS[0].key);
  // const { theme } = useTheme(); // theme from context is used in ThemeSwitcherButton and AppThemeProvider handles the class on <html>

  const ActivePageComponent = TABS.find(tab => tab.key === activeTab)?.component;

  return (
    // Tailwind's dark mode is controlled by a class on the <html> element, managed by AppThemeProvider
    <div className="app-container min-h-screen flex flex-col bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100 transition-colors duration-300">
      <header className="p-3 bg-white dark:bg-gray-800 shadow-md sticky top-0 z-50">
        <div className="container mx-auto flex justify-between items-center">
          <nav className="flex space-x-1 sm:space-x-2">
            {TABS.map(tab => (
              <button
                key={tab.key}
                onClick={() => setActiveTab(tab.key)}
                aria-current={activeTab === tab.key ? 'page' : undefined}
                className={`px-3 py-2 sm:px-4 sm:py-2 rounded-t-md text-sm font-medium transition-all duration-150 focus:outline-none focus-visible:ring-2 focus-visible:ring-primary-500 dark:focus-visible:ring-primary-400
                  ${
                    activeTab === tab.key
                      ? 'bg-primary-500 text-white dark:bg-primary-600 dark:text-white shadow-sm' 
                      : 'text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700 hover:text-gray-900 dark:hover:text-gray-50'
                  }
                `}
              >
                {tab.label}
              </button>
            ))}
          </nav>
          <ThemeSwitcherButton />
        </div>
      </header>

      <main className="flex-grow p-4 container mx-auto w-full">
        <Suspense 
          fallback={
            <div className="flex justify-center items-center h-64">
              <p className="text-lg text-gray-500 dark:text-gray-400">–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Å—Ç–æ—Ä—ñ–Ω–∫–∏...</p>
              {/* You could add a spinner icon here */}
            </div>
          }
        >
          {ActivePageComponent && <ActivePageComponent />}
        </Suspense>
      </main>
    </div>
  );
};

const App: React.FC = () => {
  return (
    // Using basename for potential deployment in a subdirectory
    <Router basename={(process.env.PUBLIC_URL || '').replace(/^\/\//, '/')}> 
      <AppThemeProvider>
        <GlobalStyle /> 
        <Routes>
          {/* For now, all paths lead to AppContent which handles tab navigation internally */}
          {/* In future, you could have routes like /products, /orders etc. that directly set the active tab */}
          <Route path="/*" element={<AppContent />} /> 
        </Routes>
      </AppThemeProvider>
    </Router>
  );
};

export default App;
