import React, { createContext, useContext, useState } from 'react';
import { BrowserRouter } from 'react-router-dom';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFilter, faSearch } from '@fortawesome/free-solid-svg-icons';
import ProductsTab from './pages/ProductsTab';
import OrdersTab from './pages/OrdersTab';
import ProductFilters from './components/filters/ProductFilters';
import OrderFilters from './components/filters/OrderFilters';

// Створюємо контекст пошуку
interface SearchContextType {
  searchTerm: string;
  setSearchTerm: React.Dispatch<React.SetStateAction<string>>;
}

const SearchContext = createContext<SearchContextType>({
  searchTerm: '',
  setSearchTerm: () => {},
});

export const useSearch = () => useContext(SearchContext);

// Створюємо контекст фільтрів
interface FilterContextType {
  sidebarOpen: boolean;
  setSidebarOpen: React.Dispatch<React.SetStateAction<boolean>>;
}

const FilterContext = createContext<FilterContextType>({
  sidebarOpen: true,
  setSidebarOpen: () => {},
});

export const useFilter = () => useContext(FilterContext);

// Стильові компоненти
const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background-color: #f5f5f5;
`;

const Header = styled.header`
  display: flex;
  align-items: center;
  height: 60px;
  padding: 0 20px;
  background-color: #f0f0f0;
  color: #333;
  border-bottom: 1px solid #ddd;
`;

const TabContainer = styled.div`
  display: flex;
  gap: 1px;
  margin-left: 20px;
  height: 100%;
  align-items: flex-end;
`;

const Tab = styled.div<{ active: boolean }>`
  padding: 8px 20px;
  height: ${props => props.active ? '36px' : '32px'};
  display: flex;
  align-items: center;
  cursor: pointer;
  border-radius: 8px 8px 0 0;
  background-color: ${props => props.active ? 'white' : '#e8e8e8'};
  color: #333;
  font-weight: ${props => props.active ? 'bold' : 'normal'};
  border: 1px solid #ddd;
  border-bottom: ${props => props.active ? 'none' : '1px solid #ddd'};
  position: relative;
  bottom: ${props => props.active ? '-1px' : '0'};
  z-index: ${props => props.active ? '1' : '0'};

  &:hover {
    background-color: ${props => props.active ? 'white' : '#e0e0e0'};
  }
`;

const SearchContainer = styled.div`
  position: relative;
  flex: 1;
  max-width: 400px;
  margin: 0 20px;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 8px 15px 8px 35px;
  border-radius: 4px;
  border: 1px solid #ddd;
  background-color: white;
  color: #333;
  transition: border-color 0.2s;
  font-size: 14px;

  &:focus {
    outline: none;
    border-color: #4a6da7;
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 10px;
  top: 50%;
  transform: translateY(-50%);
  color: #888;
`;

const MainContainer = styled.div`
  display: flex;
  flex: 1;
  overflow: hidden;
`;

const Sidebar = styled.div<{ isOpen: boolean }>`
  width: ${props => props.isOpen ? '300px' : '0'};
  height: 100%;
  background-color: #f9f9f9;
  overflow-y: auto;
  transition: width 0.3s ease;
  border-right: 1px solid #e0e0e0;
  padding: ${props => props.isOpen ? '20px' : '0'};
`;

const FilterButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #f0f0f0;
  color: #333;
  border: 1px solid #ddd;
  border-radius: 4px;
  padding: 8px 12px;
  margin-left: 20px;
  cursor: pointer;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #e0e0e0;
  }

  svg {
    margin-right: 5px;
  }
`;

const ContentArea = styled.main`
  flex: 1;
  padding: 20px;
  overflow-y: auto;
  background-color: white;
`;

// Основний компонент додатку
const App: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeTab, setActiveTab] = useState<'products' | 'orders'>('products');

  return (
    <BrowserRouter>
      <SearchContext.Provider value={{ searchTerm, setSearchTerm }}>
        <FilterContext.Provider value={{ sidebarOpen, setSidebarOpen }}>
          <AppContainer>
            <Header>
              <div>Inventory Manager</div>
              <TabContainer>
                <Tab 
                  active={activeTab === 'products'} 
                  onClick={() => setActiveTab('products')}
                >
                  Товари
                </Tab>
                <Tab 
                  active={activeTab === 'orders'} 
                  onClick={() => setActiveTab('orders')}
                >
                  Замовлення
                </Tab>
              </TabContainer>
              <SearchContainer>
                <SearchIcon>
                  <FontAwesomeIcon icon={faSearch} />
                </SearchIcon>
                <SearchInput
                  type="text"
                  placeholder="Пошук..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </SearchContainer>
              <FilterButton onClick={() => setSidebarOpen(!sidebarOpen)}>
                <FontAwesomeIcon icon={faFilter} />
                Фільтри
              </FilterButton>
            </Header>
            <MainContainer>
              <Sidebar isOpen={sidebarOpen}>
                {activeTab === 'products' ? <ProductFilters /> : <OrderFilters />}
              </Sidebar>
              <ContentArea>
                {activeTab === 'products' ? <ProductsTab /> : <OrdersTab />}
              </ContentArea>
            </MainContainer>
          </AppContainer>
        </FilterContext.Provider>
      </SearchContext.Provider>
    </BrowserRouter>
  );
};

export default App;
