import React, { createContext, useContext, useState } from 'react';
import { BrowserRouter } from 'react-router-dom';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFilter, faSearch, faMoon, faSun } from '@fortawesome/free-solid-svg-icons';
import ProductsTab from './pages/ProductsTab';
import OrdersTab from './pages/OrdersTab';
import ProductFilters from './components/filters/ProductFilters';
import OrderFilters from './components/filters/OrderFilters';
import { useTheme } from './contexts/ThemeContext';

// Створюємо контекст пошуку
interface SearchContextType {
  searchTerm: string;
  setSearchTerm: React.Dispatch<React.SetStateAction<string>>;
}

const SearchContext = createContext<SearchContextType>({
  searchTerm: '',
  setSearchTerm: () => {},
});

export const useSearch = () => useContext(SearchContext);

// Створюємо контекст фільтрів
interface FilterContextType {
  sidebarOpen: boolean;
  setSidebarOpen: React.Dispatch<React.SetStateAction<boolean>>;
}

const FilterContext = createContext<FilterContextType>({
  sidebarOpen: true,
  setSidebarOpen: () => {},
});

export const useFilter = () => useContext(FilterContext);

// Стильові компоненти
const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background-color: #f5f5f5;
`;

const TopNav = styled.div`
  display: flex;
  background-color: #f0f0f0;
  border-bottom: 1px solid #ddd;
`;

const TabContainer = styled.div`
  display: flex;
  height: 40px;
`;

const Tab = styled.div<{ active: boolean }>`
  padding: 0 20px;
  height: 40px;
  display: flex;
  align-items: center;
  cursor: pointer;
  background-color: ${props => props.active ? 'white' : '#e8e8e8'};
  color: #333;
  font-weight: ${props => props.active ? 'bold' : 'normal'};
  border-right: 1px solid #ddd;
  border-bottom: ${props => props.active ? 'none' : '1px solid #ddd'};

  &:hover {
    background-color: ${props => props.active ? 'white' : '#e0e0e0'};
  }
`;

const Header = styled.header`
  display: flex;
  align-items: center;
  height: 50px;
  padding: 0 20px;
  background-color: white;
  color: #333;
  border-bottom: 1px solid #ddd;
`;

const Logo = styled.div`
  margin-right: 20px;
  display: flex;
  align-items: center;
`;

const ThemeToggle = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  color: #555;
  font-size: 20px;
  padding: 5px;
  margin-left: 10px;

  &:hover {
    color: #333;
  }
`;

const LogoImage = styled.img`
  height: 30px;
`;

const SearchContainer = styled.div`
  position: relative;
  flex: 1;
  max-width: 400px;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 8px 15px 8px 35px;
  border-radius: 4px;
  border: 1px solid #ddd;
  background-color: white;
  color: #333;
  transition: border-color 0.2s;
  font-size: 14px;

  &:focus {
    outline: none;
    border-color: #4a6da7;
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 10px;
  top: 50%;
  transform: translateY(-50%);
  color: #888;
`;

const MainContainer = styled.div`
  display: flex;
  flex: 1;
  overflow: hidden;
  position: relative;
`;

const FilterTab = styled.div<{ isOpen: boolean }>`
  position: absolute;
  left: ${props => props.isOpen ? '300px' : '0'};
  width: 20px;
  height: 60px;
  background-color: ${props => props.isOpen ? '#f9f9f9' : '#e8e8e8'};
  border: 1px solid #ddd;
  border-left: ${props => props.isOpen ? '1px solid #ddd' : 'none'};
  border-radius: 0 4px 4px 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  padding: 10px 0;
  z-index: 100;
  transition: left 0.3s ease;
  font-size: 12px;
  box-shadow: 1px 1px 3px rgba(0,0,0,0.1);

  &:hover {
    background-color: ${props => props.isOpen ? '#f0f0f0' : '#d5d5d5'};
  }
`;

const DragHandle = styled.div`
  width: 100%;
  height: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: grab;

  &:active {
    cursor: grabbing;
  }

  &::before {
    content: '';
    width: 10px;
    height: 2px;
    background-color: #aaa;
    border-radius: 1px;
  }
`;

const FilterIcon = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex: 1;
  width: 100%;
  cursor: pointer;
`;

const Sidebar = styled.div<{ isOpen: boolean }>`
  position: absolute;
  left: ${props => props.isOpen ? '0' : '-300px'};
  width: 300px;
  height: 100%;
  background-color: #f9f9f9;
  overflow-y: auto;
  transition: left 0.3s ease;
  border-right: 1px solid #e0e0e0;
  padding: 20px;
  z-index: 10;
`;

const ContentArea = styled.main<{ sidebarOpen: boolean }>`
  margin-left: ${props => props.sidebarOpen ? '320px' : '0'};
  flex: 1;
  padding: 20px;
  overflow-y: auto;
  background-color: white;
  transition: margin-left 0.3s ease;
`;

// Компонент для App з іконками теми
const AppWithTheme: React.FC = () => {
  const { darkTheme, toggleTheme } = useTheme();
  const [searchTerm, setSearchTerm] = useState('');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [activeTab, setActiveTab] = useState<'products' | 'orders'>('products');
  const [tabPosition, setTabPosition] = useState(80);
  const [isDragging, setIsDragging] = useState(false);
  const [dragStartY, setDragStartY] = useState(0);
  const [moveThreshold] = useState(5); // Поріг для визначення чи це перетягування
  const [hasExceededThreshold, setHasExceededThreshold] = useState(false);

  // Обробники для перетягування закладки
  const handleDragStart = (e: React.MouseEvent) => {
    e.preventDefault();
    setIsDragging(true);
    setDragStartY(e.clientY);
    setHasExceededThreshold(false);
  };

  const handleDragEnd = () => {
    setIsDragging(false);
    setHasExceededThreshold(false);
  };

  const handleDrag = (e: MouseEvent) => {
    if (isDragging) {
      const moveDistance = Math.abs(e.clientY - dragStartY);

      // Лише якщо перевищили поріг руху, починаємо переміщення
      if (moveDistance > moveThreshold || hasExceededThreshold) {
        setHasExceededThreshold(true);
        const newPosition = Math.max(50, Math.min(window.innerHeight - 120, e.clientY));
        setTabPosition(newPosition);
      }
    }
  };

  const handleTabClick = () => {
    if (!hasExceededThreshold) {
      setSidebarOpen(!sidebarOpen);
    }
  };

  React.useEffect(() => {
    if (isDragging) {
      window.addEventListener('mousemove', handleDrag);
      window.addEventListener('mouseup', handleDragEnd);
    } else {
      window.removeEventListener('mousemove', handleDrag);
      window.removeEventListener('mouseup', handleDragEnd);
    }

    return () => {
      window.removeEventListener('mousemove', handleDrag);
      window.removeEventListener('mouseup', handleDragEnd);
    };
  }, [isDragging, dragStartY, hasExceededThreshold]);

  return (
    <SearchContext.Provider value={{ searchTerm, setSearchTerm }}>
      <FilterContext.Provider value={{ sidebarOpen, setSidebarOpen }}>
        <AppContainer>
          <TopNav>
            <TabContainer>
              <Tab 
                active={activeTab === 'products'} 
                onClick={() => setActiveTab('products')}
              >
                Товари
              </Tab>
              <Tab 
                active={activeTab === 'orders'} 
                onClick={() => setActiveTab('orders')}
              >
                Замовлення
              </Tab>
            </TabContainer>
          </TopNav>
          <Header>
            <Logo>
              <LogoImage 
                src={darkTheme ? "/images/logo_dark.png" : "/images/logo.png"} 
                alt="Logo" 
              />
              <ThemeToggle onClick={toggleTheme}>
                <FontAwesomeIcon icon={darkTheme ? faSun : faMoon} />
              </ThemeToggle>
            </Logo>
            <SearchContainer>
              <SearchIcon>
                <FontAwesomeIcon icon={faSearch} />
              </SearchIcon>
              <SearchInput
                type="text"
                placeholder="Пошук..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </SearchContainer>
          </Header>
          <MainContainer>
            <Sidebar isOpen={sidebarOpen}>
              {activeTab === 'products' ? <ProductFilters /> : <OrderFilters />}
            </Sidebar>
            <FilterTab 
              isOpen={sidebarOpen} 
              style={{ top: `${tabPosition}px` }}
            >
              <DragHandle 
                onMouseDown={handleDragStart}
                title="Перетягніть для зміни положення"
              />
              <FilterIcon onClick={handleTabClick}>
                <FontAwesomeIcon icon={faFilter} />
              </FilterIcon>
              <DragHandle 
                onMouseDown={handleDragStart}
                title="Перетягніть для зміни положення"
              />
            </FilterTab>
            <ContentArea sidebarOpen={sidebarOpen}>
              {activeTab === 'products' ? <ProductsTab /> : <OrdersTab />}
            </ContentArea>
          </MainContainer>
        </AppContainer>
      </FilterContext.Provider>
    </SearchContext.Provider>
  );
};

// Обгортка для контексту теми
const App: React.FC = () => {
  return (
    <BrowserRouter>
      <AppWithTheme />
    </BrowserRouter>
  );
};

export default App;
