import React, { useState } from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';
import styled from 'styled-components';

import Header from './components/common/Header';
import Sidebar from './components/common/Sidebar';
import ProductsTab from './pages/ProductsTab';
import OrdersTab from './pages/OrdersTab';
import ParsingDialog from './components/parsing/ParsingDialog';
import { useTheme } from './contexts/ThemeContext';

const AppContainer = styled.div<{ isDarkTheme: boolean }>`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  background-color: ${props => props.isDarkTheme ? 'var(--dark-bg-color)' : 'var(--light-bg-color)'};
  color: ${props => props.isDarkTheme ? 'var(--dark-text-color)' : 'var(--light-text-color)'};
`;

const MainContainer = styled.div`
  display: flex;
  flex: 1;
`;

const ContentArea = styled.main`
  flex: 1;
  padding: 20px;
  overflow: auto;
`;

function App() {
  const { darkTheme } = useTheme();
  const [showParsingDialog, setShowParsingDialog] = useState(false);
  const location = useLocation();

  const handleOpenParsingDialog = () => {
    setShowParsingDialog(true);
  };

  const handleCloseParsingDialog = () => {
    setShowParsingDialog(false);
  };

  return (
    <AppContainer isDarkTheme={darkTheme} className={darkTheme ? 'dark-theme' : 'light-theme'}>
      <Header onOpenParsingDialog={handleOpenParsingDialog} />
      <MainContainer>
        <Sidebar activePath={location.pathname} />
        <ContentArea>
          <Routes>
            <Route path="/products" element={<ProductsTab />} />
            <Route path="/orders" element={<OrdersTab />} />
            <Route path="/" element={<Navigate to="/products" replace />} />
          </Routes>
        </ContentArea>
      </MainContainer>

      {showParsingDialog && (
        <ParsingDialog 
          open={showParsingDialog} 
          onClose={handleCloseParsingDialog} 
        />
      )}
    </AppContainer>
  );
}

export default App;
